class NewsResponse {
  String? status;
  num? totalResults;
  List<News>? newsList;
  String? code;
  String? message;
  NewsResponse(
      {this.status, this.totalResults, this.newsList, this.message, this.code});

  NewsResponse.fromJson(dynamic json) {
    status = json['status'];
    totalResults = json['totalResults'];
    code = json['code'];
    message = json['message'];
    if (json['articles'] != null) {
      newsList = [];
      json['articles'].forEach((v) {
        newsList?.add(News.fromJson(v));
      });
    }
  }

  NewsResponse copyWith({
    String? status,
